            }else{
                offer.comments.comments.push(comment.comments[0])
                await offer.save()
            }
            return {
                state: true,
                message: 'Comment added successfully'
            }
        }catch(error){
            return {
                state: false,
                message: error.message
            }                                                               
        }
    }

